import React, { useEffect, useState } from 'react';

const Leaderboard = () => {
  const [data, setData] = useState([]);
  const [selectedGame, setSelectedGame] = useState('All');
  const [editId, setEditId] = useState(null);
  const [editData, setEditData] = useState({ name: '', score: '', game: '' });

  // Fetch data
  const fetchData = () => {
    fetch("http://localhost/leaderboard-api/read.php")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        console.error("Error fetching leaderboard:", error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Delete
  const handleDelete = (id) => {
    if (window.confirm("Hapus data ini?")) {
      fetch("http://localhost/leaderboard-api/delete.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id })
      })
        .then(res => res.json())
        .then(() => fetchData());
    }
  };

  // Start Edit
  const handleEdit = (item) => {
    setEditId(item.id);
    setEditData({ name: item.name, score: item.score, game: item.game });
  };

  // Save Edit
  const handleSave = () => {
    fetch("http://localhost/leaderboard-api/update.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: editId,
        nama: editData.name,
        skor: editData.score,
        game: editData.game
      })
    })
      .then(res => res.json())
      .then(() => {
        setEditId(null);
        setEditData({ name: '', score: '', game: '' });
        fetchData();
      });
  };

  const filteredData = selectedGame === 'All'
    ? data
    : data.filter(item => item.game === selectedGame);

  const uniqueGames = [...new Set(data.map(item => item.game))];

  return (
    <div className="p-4 text-white">
      <h1 className="text-2xl font-bold mb-4">🏆 Leaderboard</h1>

      <div className="mb-4">
        <label className="mr-2 font-semibold">Filter Game:</label>
        <select
          value={selectedGame}
          onChange={(e) => setSelectedGame(e.target.value)}
          className="text-black p-1 rounded"
        >
          <option value="All">All</option>
          {uniqueGames.map((game, i) => (
            <option key={i} value={game}>{game}</option>
          ))}
        </select>
      </div>

      <ul className="space-y-2">
        {filteredData.map((item, index) => (
          <li key={item.id} className="bg-gray-800 p-3 rounded shadow flex justify-between items-center">
            {editId === item.id ? (
              <>
                <div className="flex flex-col gap-1">
                  <input
                    className="text-black px-2 py-1 rounded"
                    value={editData.name}
                    onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                  />
                  <input
                    type="number"
                    className="text-black px-2 py-1 rounded"
                    value={editData.score}
                    onChange={(e) => setEditData({ ...editData, score: e.target.value })}
                  />
                  <input
                    className="text-black px-2 py-1 rounded"
                    value={editData.game}
                    onChange={(e) => setEditData({ ...editData, game: e.target.value })}
                  />
                </div>
                <div className="space-x-2">
                  <button
                    className="bg-green-600 px-2 py-1 rounded"
                    onClick={handleSave}
                  >
                    Simpan
                  </button>
                  <button
                    className="bg-gray-500 px-2 py-1 rounded"
                    onClick={() => setEditId(null)}
                  >
                    Batal
                  </button>
                </div>
              </>
            ) : (
              <>
                <span>{index + 1}. {item.name} - {item.score} ({item.game})</span>
                <div className="space-x-2">
                  <button
                    className="bg-yellow-500 px-2 py-1 rounded"
                    onClick={() => handleEdit(item)}
                  >
                    Edit
                  </button>
                  <button
                    className="bg-red-600 px-2 py-1 rounded"
                    onClick={() => handleDelete(item.id)}
                  >
                    Delete
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Leaderboard;
