import React, { useEffect, useState } from 'react';

const Leaderboard = () => {
  const [data, setData] = useState([]);
  const [filteredGame, setFilteredGame] = useState("all");
  const [games, setGames] = useState([]);
  const [editItem, setEditItem] = useState(null); // item sedang diedit

  const fetchData = () => {
    fetch("http://localhost/leaderboard-api/read.php")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
        const uniqueGames = [...new Set(data.map(item => item.game))];
        setGames(uniqueGames);
      })
      .catch((error) => {
        console.error("Error fetching leaderboard:", error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const filteredData = filteredGame === "all"
    ? data
    : data.filter((item) => item.game === filteredGame);

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this entry?")) {
      fetch("http://localhost/leaderboard-api/delete.php", {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id })
      })
        .then(res => res.json())
        .then(res => {
          alert(res.message || "Deleted successfully.");
          fetchData();
        })
        .catch(err => console.error("Delete error:", err));
    }
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost/leaderboard-api/update.php", {
      method: "POST",
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editItem)
    })
      .then(res => res.json())
      .then(res => {
        alert(res.message || "Updated successfully.");
        setEditItem(null);
        fetchData();
      })
      .catch(err => console.error("Update error:", err));
  };

  return (
    <div className="p-6 text-white bg-black min-h-screen">
      <h1 className="text-3xl font-bold mb-4">üèÜ Leaderboard</h1>

      {/* Filter Game */}
      <div className="mb-4">
        <label className="mr-2">Filter by Game:</label>
        <select
          className="bg-gray-800 text-white border border-gray-600 px-3 py-1 rounded"
          value={filteredGame}
          onChange={(e) => setFilteredGame(e.target.value)}
        >
          <option value="all">All</option>
          {games.map((game, idx) => (
            <option key={idx} value={game}>{game}</option>
          ))}
        </select>
      </div>

      {/* Tabel */}
      <table className="w-full border border-gray-600">
        <thead>
          <tr className="bg-gray-800">
            <th className="border px-3 py-2">#</th>
            <th className="border px-3 py-2">Name</th>
            <th className="border px-3 py-2">Score</th>
            <th className="border px-3 py-2">Game</th>
            <th className="border px-3 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item, index) => (
            <tr key={item.id}>
              <td className="border px-3 py-2 text-center">{index + 1}</td>
              <td className="border px-3 py-2">{item.nama}</td>
              <td className="border px-3 py-2">{item.skor}</td>
              <td className="border px-3 py-2">{item.game}</td>
              <td className="border px-3 py-2 flex gap-2 justify-center">
                <button
                  className="bg-yellow-500 text-black px-2 py-1 rounded"
                  onClick={() => setEditItem(item)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-600 px-2 py-1 rounded"
                  onClick={() => handleDelete(item.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Form Edit */}
      {editItem && (
        <form onSubmit={handleEditSubmit} className="mt-6 bg-gray-900 p-4 rounded">
          <h2 className="text-xl font-semibold mb-2">Edit Entry</h2>
          <div className="mb-2">
            <label className="block">Name:</label>
            <input
              type="text"
              className="w-full bg-gray-800 border border-gray-600 text-white px-2 py-1 rounded"
              value={editItem.nama}
              onChange={(e) => setEditItem({ ...editItem, nama: e.target.value })}
              required
            />
          </div>
          <div className="mb-2">
            <label className="block">Score:</label>
            <input
              type="number"
              className="w-full bg-gray-800 border border-gray-600 text-white px-2 py-1 rounded"
              value={editItem.skor}
              onChange={(e) => setEditItem({ ...editItem, skor: e.target.value })}
              required
            />
          </div>
          <div className="mb-2">
            <label className="block">Game:</label>
            <select
              className="w-full bg-gray-800 border border-gray-600 text-white px-2 py-1 rounded"
              value={editItem.game}
              onChange={(e) => setEditItem({ ...editItem, game: e.target.value })}
              required
            >
              {games.map((g, idx) => (
                <option key={idx} value={g}>{g}</option>
              ))}
            </select>
          </div>
          <div className="flex gap-2 mt-2">
            <button type="submit" className="bg-green-500 px-3 py-1 rounded text-black">Save</button>
            <button type="button" className="bg-gray-500 px-3 py-1 rounded" onClick={() => setEditItem(null)}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default Leaderboard;
